package ex02;

//객체지향적 프로그래밍
public class MyCalculator {

		// TODO Auto-generated method stub
		// 데이터(변수-멤버필드)
		int x;
		int y;
		
		
		// 기능(함수-멤버메서드)
		
		// 덧셈
		int add() {
			return x+y;
		}
		// 뺄셈
		int sub() {
			return x-y;
		}
		// 곱하기
		int mul() {
			return x*y;
		}
			// 나누기
		int div() {
			return x/y;
		}
			// 나머지
		int mod() {
			return x%y;
		}

}
